import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function DiabeticPredictionForm() {
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    age: '',
    bs_fast: '',
    bs_pp: '',
    plasma_r: '',
    plasma_f: '',
    hbA1c: ''
  });
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) navigate('/login');
  }, [navigate]);

  const handleChange = e => {
    setFormData(prev => ({
      ...prev,
      [e.target.name]: e.target.value
    }));
  };

  const handleSubmit = async e => {
    e.preventDefault();
    if (loading) return;

    setError(null);
    setLoading(true);

    try {
      const token = localStorage.getItem('token');
      const response = await axios.post(
        'http://127.0.0.1:8000/acc/predict/',
        formData,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      if (response.data.Error === '0') {
        navigate('/diabetic-result', { state: { input: formData, result: response.data } });
      } else {
        setError(response.data.Message || 'Prediction failed.');
      }
    } catch (err) {
      if (err.response?.status === 401) {
        setError('Unauthorized. Please log in again.');
        navigate('/login');
      } else {
        setError('Prediction failed. Please check the input or server.');
      }
    }

    setLoading(false);
  };

  const fields = [
    { label: 'Age', name: 'age', placeholder: '18‚Äì100' },
    { label: 'BS Fast', name: 'bs_fast', placeholder: '70‚Äì130 mg/dL' },
    { label: 'BS PP', name: 'bs_pp', placeholder: '100‚Äì180 mg/dL' },
    { label: 'Plasma Random', name: 'plasma_r', placeholder: '80‚Äì200 mg/dL' },
    { label: 'Plasma Fasting', name: 'plasma_f', placeholder: '70‚Äì110 mg/dL' },
    { label: 'HbA1c', name: 'hbA1c', placeholder: '4.0‚Äì14.0 %' }
  ];

  return (
    <>
      <nav className="navbar navbar-expand-lg navbar-dark bg-success">
        <div className="container">
          <a className="navbar-brand fw-bold" href="/">üè• Health Predictor</a>
          <button className="btn btn-light btn-sm" onClick={() => navigate('/')}>‚¨Ö Home</button>
        </div>
      </nav>

      <div className="container d-flex justify-content-center align-items-start" style={{ minHeight: '90vh', background: '#f1f8ff', paddingTop: '40px' }}>
        <div className="bg-white shadow-lg p-4 p-md-5 rounded-4" style={{ width: '100%', maxWidth: '720px' }}>
          <h3 className="text-center fw-bold mb-4 text-success">ü©∫ Diabetic Type Predictor</h3>

          <form onSubmit={handleSubmit}>
            <div className="row g-3">
              {fields.map(({ label, name, placeholder }) => (
                <div className="col-md-6" key={name}>
                  <label className="form-label fw-semibold">{label}</label>
                  <input
                    type="number"
                    step="any"
                    name={name}
                    value={formData[name]}
                    onChange={handleChange}
                    className="form-control"
                    placeholder={placeholder}
                    required
                  />
                </div>
              ))}
            </div>

            <button type="submit" className="btn mt-4 w-100 fw-bold text-white" style={{ backgroundColor: '#0d47a1' }}>
              {loading ? '‚è≥ Predicting...' : 'üöÄ Predict'}
            </button>
          </form>

          {error && <div className="alert alert-danger mt-3 text-center">‚ùå {error}</div>}
        </div>
      </div>
    </>
  );
}

export default DiabeticPredictionForm;
