import React, { useState } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';

function DiabeticPredictionForm() {
  const [formData, setFormData] = useState({
    age: '',
    bs_fast: '',
    bs_pp: '',
    plasma_r: '',
    plasma_f: '',
    hbA1c: ''
  });

  const [result, setResult] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);
    setResult(null);
    setLoading(true);

    try {
      const response = await axios.post('http://127.0.0.1:8000/acc/predict/', formData);
      setResult(response.data);
    } catch (err) {
      setError('Prediction failed. Please check the input or server.');
    }

    setLoading(false);
  };

  return (
    <div className="container mt-5 p-4 rounded shadow" style={{ background: '#f5f9ff', maxWidth: '600px' }}>
      <h2 className="text-center text-primary mb-4">Diabetic Type Predictor</h2>
      <form onSubmit={handleSubmit}>
        {[
          { label: "Age", name: "age" },
          { label: "BS Fast", name: "bs_fast" },
          { label: "BS PP", name: "bs_pp" },
          { label: "Plasma Random", name: "plasma_r" },
          { label: "Plasma Fasting", name: "plasma_f" },
          { label: "HbA1c", name: "hbA1c" }
        ].map(({ label, name }) => (
          <div className="mb-3" key={name}>
            <label className="form-label">{label}</label>
            <input
              type="number"
              step="any"
              className="form-control"
              name={name}
              value={formData[name]}
              onChange={handleChange}
              required
            />
          </div>
        ))}

        <div className="d-grid">
          <button type="submit" className="btn btn-primary">
            {loading ? 'Predicting...' : 'Predict'}
          </button>
        </div>
      </form>

      {result && result.Error === '0' && (
        <div className="alert alert-success mt-4">
          <h5>âœ… Prediction: <strong>{result.Prediction}</strong></h5>
          <p>Confidence: <strong>{result['Confidence Score']}</strong></p>
        </div>
      )}

      {result && result.Error !== '0' && (
        <div className="alert alert-warning mt-4">
          <strong>Error:</strong> {result.Message}
        </div>
      )}

      {error && (
        <div className="alert alert-danger mt-4">
          {error}
        </div>
      )}
    </div>
  );
}

export default DiabeticPredictionForm;
