import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function DiabeticPredictionForm() {
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    age: '',
    bs_fast: '',
    bs_pp: '',
    plasma_r: '',
    plasma_f: '',
    hbA1c: ''
  });

  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  // 🔐 Redirect to login if token is missing
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
    }
  }, [navigate]);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (loading) return;

    setError(null);
    setLoading(true);

    try {
      const token = localStorage.getItem('token');
      const response = await axios.post(
        'http://127.0.0.1:8000/acc/predict/',
        formData,
        {
          headers: {
            Authorization: `Bearer ${token}`
          }
        }
      );

      if (response.data.Error === '0') {
        navigate('/diabetic-result', {
          state: {
            input: formData,
            result: response.data
          }
        });
      } else {
        setError(response.data.Message || 'Prediction failed.');
      }

    } catch (err) {
      if (err.response?.status === 401) {
        setError('Unauthorized. Please log in again.');
        navigate('/login');
      } else {
        setError('Prediction failed. Please check the input or server.');
      }
    }

    setLoading(false);
  };

  return (
    <>
  {/* 🔼 Navbar */}
  <nav className="navbar navbar-expand-lg navbar-dark bg-success">
    <div className="container">
      <a className="navbar-brand fw-bold" href="/">🏥 Health Predictor</a>
      <button className="btn btn-light btn-sm" onClick={() => navigate('/')}>
        ⬅ Home
      </button>
    </div>
  </nav>

  {/* 🔽 Main Form with background image */}
  <div
    className="d-flex justify-content-center align-items-center vh-100"
    style={{
      backgroundImage: `url('/bg.png')`,
      backgroundSize: 'cover',
      backgroundPosition: 'center',
      backgroundRepeat: 'no-repeat',
    }}
  >
    {/* Form card with slight white transparency */}
    <div
      className="p-4 rounded-4 shadow-sm"
      style={{
        background: 'rgba(255, 255, 255, 0.85)',
        width: '100%',
        maxWidth: '400px',
      }}
    >
      <h3 className="text-center fw-bold mb-3" style={{ color: '#0d47a1' }}>
        🩺 Diabetic Type Predictor
      </h3>
      <form onSubmit={handleSubmit}>
        {[{ label: "Age", name: "age" },
          { label: "BS Fast", name: "bs_fast" },
          { label: "BS PP", name: "bs_pp" },
          { label: "Plasma Random", name: "plasma_r" },
          { label: "Plasma Fasting", name: "plasma_f" },
          { label: "HbA1c", name: "hbA1c" }
        ].map(({ label, name }) => (
          <div className="form-floating mb-2" key={name}>
            <input
              type="number"
              step="any"
              className="form-control"
              id={name}
              name={name}
              placeholder={label}
              value={formData[name]}
              onChange={handleChange}
              required
            />
            <label htmlFor={name}>{label}</label>
          </div>
        ))}

        <button
          type="submit"
          className="btn w-100 fw-bold mt-2"
          style={{ backgroundColor: '#1565c0', color: 'white' }}
        >
          {loading ? '⏳ Predicting...' : '🚀 Predict'}
        </button>
      </form>

      {error && (
        <div className="alert alert-danger mt-3">
          ❌ {error}
        </div>
      )}
    </div>
  </div>
</>

  );
}

export default DiabeticPredictionForm;
