import React, { useState } from 'react';
import axios from 'axios';

function DiabeticPredictionForm() {
  const [formData, setFormData] = useState({
    age: '',
    bs_fast: '',
    bs_pp: '',
    plasma_r: '',
    plasma_f: '',
    hbA1c: ''
  });

  const [result, setResult] = useState(null);
  const [error, setError] = useState(null);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);
    setResult(null);

    try {
      const response = await axios.post('http://127.0.0.1:8000/acc/predict/', formData);
      setResult(response.data);
    } catch (err) {
      setError('Prediction failed. Please check the input or server.');
    }
  };

  return (
    <div style={{ maxWidth: 500, margin: 'auto' }}>
      <h2>Diabetic Type Predictor</h2>
      <form onSubmit={handleSubmit}>
        <label>Age:</label>
        <input type="number" name="age" value={formData.age} onChange={handleChange} required /><br />

        <label>BS Fast:</label>
        <input type="number" name="bs_fast" value={formData.bs_fast} onChange={handleChange} required /><br />

        <label>BS PP:</label>
        <input type="number" name="bs_pp" value={formData.bs_pp} onChange={handleChange} required /><br />

        <label>Plasma Random:</label>
        <input type="number" name="plasma_r" value={formData.plasma_r} onChange={handleChange} required /><br />

        <label>Plasma Fasting:</label>
        <input type="number" name="plasma_f" value={formData.plasma_f} onChange={handleChange} required /><br />

        <label>HbA1c:</label>
        <input type="number" name="hbA1c" value={formData.hbA1c} onChange={handleChange} required /><br />

        <button type="submit">Predict</button>
      </form>

      {result && result.Error === '0' && (
        <div style={{ marginTop: 20 }}>
          <h3>Prediction: {result.Prediction}</h3>
          <p>Confidence: {result['Confidence Score']}</p>
        </div>
      )}

      {result && result.Error !== '0' && (
        <div style={{ color: 'red', marginTop: 20 }}>
          <p>{result.Message}</p>
        </div>
      )}

      {error && (
        <div style={{ color: 'red', marginTop: 20 }}>
          <p>{error}</p>
        </div>
      )}
    </div>
  );
}

export default DiabeticPredictionForm;
