import React, { useState } from 'react';
import axios from 'axios';

const HeartPredictionForm = () => {
  const [formData, setFormData] = useState({
    age: '', sex: '', cp: '', trestbps: '', chol: '',
    fbs: '', restecg: '', thalach: '', exang: '',
    oldpeak: '', slope: '', ca: '', thal: ''
  });

  const [result, setResult] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setResult(null);
    setError(null);
    setLoading(true);

    try {
      const response = await axios.post('/api/heart-predict/', formData);
      setResult(response.data);
      setTimeout(() => setResult(null), 30000); // auto-hide after 30 sec
    } catch (err) {
      setError("‚ùå Error: Invalid input or server issue.");
    } finally {
      setLoading(false);
    }
  };

  const fields = [
    ["age", "Age"], ["sex", "Sex (1=Male, 0=Female)"], ["cp", "Chest Pain Type (0-3)"],
    ["trestbps", "Resting BP"], ["chol", "Cholesterol"], ["fbs", "Fasting BS (1/0)"],
    ["restecg", "Resting ECG (0-2)"], ["thalach", "Max Heart Rate"],
    ["exang", "Exercise Angina (1/0)"], ["oldpeak", "ST Depression"],
    ["slope", "Slope (0-2)"], ["ca", "Major Vessels (0-3)"], ["thal", "Thal (1=Norm,6=Fix,7=Rev)"]
  ];

  return (
    <div className="d-flex justify-content-center align-items-center vh-100" style={{ background: '#e3f2fd' }}>
      <div className="p-4 rounded-4 shadow" style={{ backgroundColor: '#ffffff', width: '100%', maxWidth: '450px' }}>
        <h3 className="text-center mb-4 fw-bold text-success">‚ù§Ô∏è Heart Disease Predictor</h3>

        <form onSubmit={handleSubmit}>
          {fields.map(([name, label]) => (
            <div className="form-floating mb-2" key={name}>
              <input
                type="number"
                step="any"
                className="form-control"
                id={name}
                name={name}
                placeholder={label}
                value={formData[name]}
                onChange={handleChange}
                required
              />
              <label htmlFor={name}>{label}</label>
            </div>
          ))}

          <button type="submit" className="btn btn-success w-100 fw-bold mt-2">
            {loading ? '‚è≥ Predicting...' : 'üöÄ Predict'}
          </button>
        </form>

        {result && (
          <div className="alert alert-info mt-4 text-center">
            <h5>{result.result}</h5>
            <p className="mb-0">üîç Confidence: <strong>{result.confidence}</strong></p>
          </div>
        )}

        {error && (
          <div className="alert alert-danger mt-3">{error}</div>
        )}
      </div>
    </div>
  );
};

export default HeartPredictionForm;
