import React, { useState, useEffect } from 'react';
import axios from 'axios';

function DiabeticPredictionForm() {
  const [formData, setFormData] = useState({
    age: '',
    bs_fast: '',
    bs_pp: '',
    plasma_r: '',
    plasma_f: '',
    hbA1c: ''
  });

  const [result, setResult] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  // Auto-clear result after 30 sec
  useEffect(() => {
    if (result) {
      const timer = setTimeout(() => setResult(null), 20000);
      return () => clearTimeout(timer);
    }
  }, [result]);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);
    setResult(null);
    setLoading(true);

    try {
      const response = await axios.post('http://127.0.0.1:8000/acc/predict/', formData);
      setResult(response.data);
    } catch (err) {
      setError('Prediction failed. Please check the input or server.');
    }

    setLoading(false);
  };

  return (
    <div className="d-flex justify-content-center align-items-center vh-100" style={{ background: '#e3f2fd' }}>
      <div className="p-4 rounded-4 shadow-sm" style={{ background: '#ffffff', width: '100%', maxWidth: '400px' }}>
        <h3 className="text-center fw-bold mb-3" style={{ color: '#0d47a1' }}>🩺 Diabetic Type Predictor</h3>
        <form onSubmit={handleSubmit}>
          {[
            { label: "Age", name: "age" },
            { label: "BS Fast", name: "bs_fast" },
            { label: "BS PP", name: "bs_pp" },
            { label: "Plasma Random", name: "plasma_r" },
            { label: "Plasma Fasting", name: "plasma_f" },
            { label: "HbA1c", name: "hbA1c" }
          ].map(({ label, name }) => (
            <div className="form-floating mb-2" key={name}>
              <input
                type="number"
                step="any"
                className="form-control"
                id={name}
                name={name}
                placeholder={label}
                value={formData[name]}
                onChange={handleChange}
                required
              />
              <label htmlFor={name}>{label}</label>
            </div>
          ))}

          <button type="submit" className="btn w-100 fw-bold mt-2" style={{ backgroundColor: '#1565c0', color: 'white' }}>
            {loading ? '⏳ Predicting...' : '🚀 Predict'}
          </button>
        </form>

        {result && (
          <div
            className="mt-4 p-3 rounded-3 text-white text-center"
            style={{
              backgroundColor: result.Error === '0' ? '#2e7d32' : '#d32f2f',
              boxShadow: '0 0 6px rgba(0,0,0,0.2)'
            }}
          >
            {result.Error === '0' ? (
              <>
                <h5>🎯 <strong>{result.Prediction}</strong></h5>
                <p>📊 Confidence: <strong>{result['Confidence Score']}</strong></p>
              </>
            ) : (
              <p>⚠️ {result.Message}</p>
            )}
          </div>
        )}

        {error && (
          <div className="alert alert-danger mt-3">
            ❌ {error}
          </div>
        )}
      </div>
    </div>
  );
}

export default DiabeticPredictionForm;
