import React, { useState } from 'react';
import axios from 'axios';

function DiabeticPredictionForm() {
  const [formData, setFormData] = useState({
    age: '',
    bs_fast: '',
    bs_pp: '',
    plasma_r: '',
    plasma_f: '',
    hbA1c: ''
  });

  const [result, setResult] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);
    setResult(null);
    setLoading(true);

    try {
      const response = await axios.post('http://127.0.0.1:8000/acc/predict/', formData);
      setResult(response.data);
    } catch (err) {
      setError('Prediction failed. Please check the input or server.');
    }

    setLoading(false);
  };

  return (
    <div className="d-flex justify-content-center align-items-center vh-100" style={{ background: '#e8f0ff' }}>
      <div className="p-5 rounded-4 shadow-lg" style={{ background: '#ffffff', maxWidth: '500px', width: '100%' }}>
        <h2 className="text-center fw-bold mb-4" style={{ color: '#0056b3' }}>üî¨ Diabetic Type Predictor</h2>
        <form onSubmit={handleSubmit}>
          {[
            { label: "Age", name: "age" },
            { label: "BS Fast", name: "bs_fast" },
            { label: "BS PP", name: "bs_pp" },
            { label: "Plasma Random", name: "plasma_r" },
            { label: "Plasma Fasting", name: "plasma_f" },
            { label: "HbA1c", name: "hbA1c" }
          ].map(({ label, name }) => (
            <div className="form-floating mb-3" key={name}>
              <input
                type="number"
                step="any"
                className="form-control"
                id={name}
                name={name}
                placeholder={label}
                value={formData[name]}
                onChange={handleChange}
                required
              />
              <label htmlFor={name}>{label}</label>
            </div>
          ))}

          <button type="submit" className="btn w-100 fw-bold" style={{ backgroundColor: '#1e88e5', color: 'white' }}>
            {loading ? '‚è≥ Predicting...' : 'üöÄ Predict'}
          </button>
        </form>

        {result && (
          <div
            className="mt-4 p-3 rounded-3 text-white"
            style={{
              backgroundColor: result.Error === '0' ? '#43a047' : '#e53935',
              boxShadow: '0 0 10px rgba(0,0,0,0.2)'
            }}
          >
            {result.Error === '0' ? (
              <>
                <h5>üéØ <strong>Prediction:</strong> {result.Prediction}</h5>
                <p>üìä <strong>Confidence:</strong> {result['Confidence Score']}</p>
              </>
            ) : (
              <p>‚ö†Ô∏è {result.Message}</p>
            )}
          </div>
        )}

        {error && (
          <div className="alert alert-danger mt-3">
            ‚ùå {error}
          </div>
        )}
      </div>
    </div>
  );
}

export default DiabeticPredictionForm;
